        #endregion


        public void Initialize(IEnumerable<ICell> cells)
        {
            if (cells == null) return;
            foreach (var cell in cells)
            {
                MakeCell(cell.Position, cell.Alive);
            }
        }

        public IGridSize GridSize { get; set; }

